(function(){tinymce.PluginManager.requireLangPack('rainbow');tinymce.create('tinymce.plugins.RainbowPlugin',{init:function(ed,url){ed.addCommand('mceRainbow',function(){ed.windowManager.open({file:url+'/dialog.htm',width:650+parseInt(ed.getLang('rainbow.delta_width',0)),height:300+parseInt(ed.getLang('rainbow.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('rainbow',{title:'rainbow.desc',cmd:'mceRainbow',image:url+'/img/rainbow.png'});ed.onNodeChange.add(function(ed,cm,n){})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Rainbow plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'',version:"1.0"}}});tinymce.PluginManager.add('rainbow',tinymce.plugins.RainbowPlugin)})();